import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Card, Button } from 'react-bootstrap';
import beers from '../beers';

const BeerInfoScreen = ({ match }) => {
	const [qty, setQty] = useState(1);
	const beer = beers.find((p) => p._id === match.params.id);

	const addToCartHandler = () => {};

	return (
		<>
			<Link className="backbtn btn-light my-3" to="/">
				←
			</Link>
			<Row>
				<Col md={6}>
					<Image className="infoimage" src={beer.image} alt={beer.name} fluid />
				</Col>
				<Col md={6}>
					<ListGroup variant="flush">
						<ListGroup.Item>
							<h3>{beer.name}</h3>
							<h4 className="infoprice">£{beer.price} </h4>
						</ListGroup.Item>
					</ListGroup>
					<ListGroup.Item className="infodescription">{beer.description}</ListGroup.Item>
					<ListGroup.Item className="infostock">
						<ListGroup.Item>
							<Row>
								<Col>Status:</Col>
								<Col>{beer.countInStock > 0 ? 'In Stock' : 'Out of Stock'}</Col>
							</Row>
						</ListGroup.Item>

						{beer.countInStock > 0 && (
							<ListGroup.Item>
								<Row>
									<Col>Qty</Col>
									<Col></Col>
									<Form.Control as="select" value={qty} onChange={(e) => setQty(e.target.value)}>
										{[...Array(beer.countInStock).keys()].map((x) => (
											<option key={x + 1} value={x + 1}>
												{x + 1}
											</option>
										))}
									</Form.Control>
								</Row>
							</ListGroup.Item>
						)}

						<ListGroup.Item>
							<Button onClick={addToCartHandler} className="btn-block" type="button" disabled={beer.countInStock === 0}>
								Add To Cart
							</Button>
						</ListGroup.Item>
					</ListGroup.Item>
				</Col>
			</Row>
		</>
	);
};

export default BeerInfoScreen;
